import React, { useState } from 'react';
import './App.css';

class App extends Component {

  state = {
    userInput: ''
  }

  inputChangedHandler = (event) => {
    this.setState({userInput : event.target.value});
  }

  handleSubmit = event => {
    event.preventDefault();
    setSubmitting(true);

    console.log(`
      Amount: ${amount}
      Type: ${type}
      Worth: ${worth}
      Income: ${income}
      Credit: ${credit}
    `);

   setTimeout(() => {
     setSubmitting(false);
   }, 3000)
 }

  onAmountChange = (event) => {
    setAmount(event.target.value);
    if(event.target.value<=0){
        message="Amount must be greater than zero.";
    }    
    console.log(event.target.value);
  };
  onTypeChange = (event) => {
    setType(event.target.value);
    console.log(event.target.value);
  };
  onWorthChange = (event) => {
    setWorth(event.target.value);
    console.log(event.target.value);
  };
  onIncomeChange = (event) => {
    setIncome(event.target.value);
    console.log(event.target.value);
  };
  onCreditChange = (event) => {
    setCredit(event.target.value);
    console.log(event.target.value);
  };

render(){
  return(
    <div className="wrapper">
      <h1>Lorum Ipsem Lorum Ipsem Lorum Ipsem</h1>
      {submitting &&
       <div>Submtting Form...</div>
     }

      <form onSubmit={handleSubmit}>
        <fieldset><label>

            <div class="myself"><br/>
            <div class="info">{message}</div>

            <div class="property">Investment Amount:</div>
            <div class="value"><input type="number" name="amount"
                value={amount} 
                onChange={onAmountChange} required/>
            </div><br/><br/>

            <div class="property">Investment Type:</div>
            <div class="value"><input type="text" name="type"
                 value={type}
                 onChange={onTypeChange} required/>
            </div><br/><br/>

            <div class="property">Total Net Worth </div>
            <div class="value"><input type="number" name="worth" 
                value={worth}
                onChange={onWorthChange} required/>
            </div><br/><br/>

            <div class="property">User Estimated Yearly Income </div>
            <div class="value"><input type="number" name="income" 
                value={income}
                onChange={onIncomeChange} required/>
            </div><br/><br/>

            <div class="property">User Estimated Credit Score </div>
            <div class="value"><input type="number" name="credit" min="300" max="850"
                 value={credit}
                 onChange={onCreditChange} required/>
            </div><br/><br/>

            </div>

        </label></fieldset>
        <button type="submit">Submit</button>
      </form>
    </div>
  )
}
}

export default App;
