import React, { Component } from 'react';
//import React, { useState } from 'react';
import './App.css';

class App extends Component {

  state = {
    userInput: '',
    amount:'',
    type:'',
    worth:'',
    income:'',
    credit:'',
    submitting:'',
    mesage:''

  }

  inputChangedHandler = (event) => {
    this.setState({userInput : event.target.value});
  }

  handleSubmit = (event) => {
    event.preventDefault();

    console.log(`
      Amount: ${this.state.amount}
    `);

   //setTimeout(() => {
   //  setSubmitting(false);
   //}, 3000)
 }

  onAmountChange = (event) => {
    this.state.amount=event.target.value;
    if(event.target.value<=0){
        this.state.message="Amount must be greater than zero.";
    }    
    console.log(event.target.value);
  };

render(){
  return(
    <div className="wrapper">
      <h1>Lorum Ipsem Lorum Ipsem Lorum Ipsem</h1>

      <form onSubmit={handleSubmit}>
        <fieldset><label>

            <div class="myself"><br/>
            <div class="info">{this.state.message}</div>

            <div class="property">Investment Amount:</div>
            <div class="value"><input type="number" name="amount"
                value={this.state.amount} 
                onChange={onAmountChange} required/>
            </div><br/><br/>


            </div>

        </label></fieldset>
        <button type="submit">Submit</button>
      </form>
    </div>
  )
}
}

export default App;
