import React, { Component } from 'react';
//import React, { useState } from 'react';
import './App.css';

class App extends Component {
  state = {
    amount:'',
    type:'',
    worth:'',
    income:'',
    credit:'',
    submitting:'',
    message:''
  }

  handleSubmit = (event) => {
    event.preventDefault();

    console.log(`
      Amount: ${this.state.amount}
      Type: ${this.state.type}
      Worth: ${this.state.worth}
      Income: ${this.state.income}
      Credit: ${this.state.credit}
    `);

   //setTimeout(() => {
   //  setSubmitting(false);
   //}, 3000)
 }

  onAmountChange = (event) => {
    this.setState({ amount : event.target.value })
    if(event.target.value<=0){
        this.state.message="Amount must be greater than zero.";
    }    
    console.log(event.target.value);
  };

  onTypeChange = (event) => {
    this.setState({ type : event.target.value })
    console.log(event.target.value);
  };
  onWorthChange = (event) => {
    this.setState({ worth : event.target.value })
    console.log(event.target.value);
  };
  onIncomeChange = (event) => {
    this.setState({ income : event.target.value })
    console.log(event.target.value);
  };
  onCreditChange = (event) => {
    this.setState({ credit : event.target.value })
    console.log(event.target.value);
  };

render(){
  return(
    <div className="wrapper">
      <h1>Lorum Ipsem Lorum Ipsem Lorum Ipsem</h1>

      <form onSubmit={this.handleSubmit}>
        <fieldset><label>

            <div class="myself"><br/>
            <div class="info">{this.state.message}</div>

            <div class="property">Investment Amount:</div>
            <div class="value"><input type="number" name="amount"
                value={this.state.amount} 
                onChange={this.onAmountChange} required/>
            </div><br/><br/>


            <div class="property">Investment Type:</div>
            <div class="value"><input type="text" name="type"
                 value={type}
                 onChange={onTypeChange} required/>
            </div><br/><br/>

            <div class="property">Total Net Worth </div>
            <div class="value"><input type="number" name="worth" 
                value={worth}
                onChange={onWorthChange} required/>
            </div><br/><br/>

            <div class="property">User Estimated Yearly Income </div>
            <div class="value"><input type="number" name="income" 
                value={income}
                onChange={onIncomeChange} required/>
            </div><br/><br/>

            <div class="property">User Estimated Credit Score </div>
            <div class="value"><input type="number" name="credit" min="300" max="850"
                 value={credit}
                 onChange={onCreditChange} required/>
            </div><br/><br/>

            </div>

        </label></fieldset>
        <button type="submit">Submit</button>
      </form>
    </div>
  )
}
}

export default App;
